@mixin wrapper() {
    max-width: $grid-container-width;
    padding: 0 8px; 
    margin: 0 auto;
    box-sizing: border-box;
}

@mixin w-from($from) {
    @media (min-width: $from) {
        @content;
    }
}

@mixin w-to($screen) {
    @media (max-width: $screen - 1px) {
        @content;
    }
}

@mixin w-range($screen1, $screen2) {
    @media (min-width: $screen1) and (max-width: $screen2 - 1px) {
        @content;
    }
}

