sources.scss\\_header.scss:
.header {
    @include wrapper;
    grid-area: header;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    color: $headerColor;
    width: 100%;

    &__logo {
        color: $headerLogoColor;
        font-weight: $fontWeightBold;
        font-size: 24px;

        @include w-from($screen-md) {
            font-size: 48px;
        }        
    }

    &__delimeter {
        height: 24px;
        margin-right: 12px;
        margin-left: 12px;
        background-color: $headerDelimeterColor;
        width: 1px;

        @include w-from($screen-md) {
            margin-right: 32px;
            margin-left: 32px;
        }             
    }

    &__desc {
        font-size: 12px;

        @include w-from($screen-md) {
            font-size: 18px;
        }            
    }
}

sources.scss\\_mixines.scss:
@mixin wrapper() {
    max-width: $grid-container-width;
    padding: 0 8px; 
    margin: 0 auto;
    box-sizing: border-box;
}

@mixin w-from($from) {
    @media (min-width: $from) {
        @content;
    }
}

@mixin w-to($screen) {
    @media (max-width: $screen - 1px) {
        @content;
    }
}

@mixin w-range($screen1, $screen2) {
    @media (min-width: $screen1) and (max-width: $screen2 - 1px) {
        @content;
    }
}

sources.scss\\_intro.scss:
.intro {
    @include wrapper;
    height: 72px;
    font-size: 16px;
    line-height: 21px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: $introColor;

    @include w-from($screen-md) {
        flex-direction: row;
        height: 144px;
        font-size: 24px;
        line-height: 31px;        

        span:first-child {
            margin-right: 16px;
        }
    }     
}

sources.scss\\_form.scss:
.form {
    @include wrapper;
    margin-top: 16px;

    &__row:not(:first-child) {
        margin-top: 16px;

        @include w-from($screen-md) {
            margin-top: 32px;
        }           
    }

    &__row_selector {
        display: grid;
        grid-template-columns: 80px 1fr 80px;

        @include w-from($screen-md) {
            grid-template-columns: 128px 1fr 128px; 
        }         
    }

    //  
    &__password-length {
        height: 56px;
        display: flex;
        justify-content: center;
        align-items: center;
        font-size: 32px;
        color: $fontColor;

        @include w-from($screen-md) {
            height: 64px;       
        }             
    }    

    // 
    &__row_options{
        display: grid;
        grid-template-columns: 1fr 1fr;
        grid-template-rows: 1fr 1fr;
        grid-gap: 8px;

        @include w-from($screen-md) {
            grid-gap: 16px;
        }        
    }

    &__row_password {
        display: none;
    }

    &__password {
        display: inline;
        min-height: 56px;
        display: flex;
        justify-content: center;
        align-items: center;
        text-align: center;
        border-radius: $borderRadius;
        background-color: $formPasswordBg;
        color: $formPasswordColor;
        word-break: break-word;

        @include w-from($screen-md) {
            height: 64px;       
        }             
    }

}

sources.scss\\_button.scss:
.button {
    display: inline-block;
    height: 56px;
    border: none;
    border-radius: $borderRadius;
    color: $buttonColor;
    background-color: $buttonOptionBg;
    outline: none;

    @include w-from($screen-md) {
        height: 64px;       
    }     

    &:hover {
        cursor:pointer
    }

    &:active {
        border: $buttonBorder 2px solid
    }

    &_checked {
        border: $buttonBorder 2px solid;
        color: $fontColor;
    }   
    
    &[disabled] {
        color:transparentize($colorBlack, .5)
    }
    
    &_option {
        font-size: 32px;
    }

    &_submit {
        width: 100%;
        background-color: $buttonSubmitBg;
        font-weight: $fontWeightBold;
        color: $fontColor
    }

    &_submit[disabled] {
        color: transparentize($fontColor, .5)
    }    


}

sources.scss\\_footer.scss:
.footer {
    @include wrapper;
    grid-area: footer;
    color: $fontColor;
    display: flex;
    justify-content: center;
    align-items: center;

    &__copyright {
        //
    }

    a {
        color: $fontColor;
        border-bottom: lighten($fontColor, 50%) 1px solid
    }

    a:hover {
        color: $footerLinkHoverColor;
        border-bottom: transparentize($footerLinkHoverColor, .6) 1px solid;
    }    
}
